import "./Today.css"
import SideBody from './sideBody/SideBody'
import './News.css'
import axios from 'axios';
import SimpleMap from "./googleMap/GoogleMap.js"


class Today  extends Component {

  constructor() {
      super()
      this.state = {
        loadingText:"updating...",
          searchByLL:"",
          id:"",
          city:"Lima"
      }

this.findCoordinates=this.findCoordinates.bind(this)
this.currentWeatherIcon=this.currentWeatherIcon.bind(this)
this.convertionOfURL=this.convertionOfURL.bind(this)
this.updatingState=this.updatingState.bind(this)
this.breakingDownNewsDate=this.breakingDownNewsDate.bind(this)
  }




