    convertionOfURL(a,b){
      a = this.state.todayInfo.current.condition.icon;
       b = a.substring(2, 60)
       return b
    }

      findCoordinates () {
        if(this.state.loadingText="updating..."){
 console.log("yes from cord")} else { console.log("no from cords")
}
}

breakingDownNewsDate(a){
   a = a.slice(0, 10)
   return a
}
      componentDidMount() {
        navigator.geolocation.getCurrentPosition((pos) => {
              this.setState({
                  currentPosition: pos.coords.latitude.toString() + "," + pos.coords.longitude.toString(),
                  uri:'http://api.apixu.com/v1/forecast.json?key=82fe923258fa4530a5e235322192604&q='
              });
              fetch("https://cors-anywhere.herokuapp.com/" + this.state.uri + this.state.currentPosition + '&days=7').then((res) => res.json())
              .then((todayInfo) => this.setState({
                  todayInfo,
                  currentIcon:todayInfo.current.condition.code,
                  conditionOneDayAhead:todayInfo.forecast.forecastday[1].day.condition,
                  conditionTwoDayAhead:todayInfo.forecast.forecastday[2].day.condition,
                  conditionThreeDayAhead:todayInfo.forecast.forecastday[3].day.condition
              }))
                  .catch((err) => console.log(err.message))
          });
          var url = 'https://newsapi.org/v2/top-headlines?' +
                    'country=us&' +
                    'apiKey=f8822ecd8b3d4ceb87c8b11367961689';
          var req = new Request(url);
          fetch(req)
          .then((res) => res.json())
          .then((news) => this.setState({
              news:news.articles
          })).catch((err) => console.log(err.message));
      }





      componentDidUpdate() {

}



updatingState(){

    fetch( this.state.uri + this.state.city + '&days=7' ).then((res) => res.json())
    .then((todayInfo) => this.setState({
        todayInfo
    })).catch((err) => console.log(err.message));
    console.log("updatingState")
}

  render () {
{console.log(this.state)}
{ this.state.todayInfo ? console.log(this.state.todayInfo.forecast.forecastday[1]) : console.log("noo")}
   return(

   <div className="container" >

   <div className="firstRow">
   <div className="mainContent">

   <div className="userLT"> <p>
      {this.state.todayInfo ? this.state.todayInfo.location.name : this.state.loading},
      {this.state.todayInfo ? this.state.todayInfo.location.region : this.state.loading},
      {this.state.todayInfo ? this.state.todayInfo.location.country : this.state.loading}  </p>
       <span> Last updated {this.state.todayInfo ? this.state.todayInfo.current.last_updated : this.state.loading}</span> </div>

   <div className="userWeather">

    <div className="temperature">  <h3> {this.state.todayInfo ? this.state.todayInfo.current.temp_f : this.state.loading}&#176; </h3>
     <h4> {this.state.todayInfo ? this.state.todayInfo.current.condition.text : this.state.loading}</h4>

    <div className="extraInfo">
    <ul>
    <li> <p>feels like <span> {this.state.todayInfo ? this.state.todayInfo.current.feelslike_f : this.state.loading}&#176;</span> </p> </li>
    <li> <p> UV light <span>{this.state.todayInfo ? this.state.todayInfo.current.uv: this.state.loading}</span>  out of<span> 10</span> </p> </li>
    <li> <p> humidity <span> {this.state.todayInfo ? this.state.todayInfo.current.humidity: this.state.loading}&#37;</span> </p></li>
    </ul>
     </div>
      </div>
     <div className="weatherImg">
      <img src={this.state.todayInfo ? this.currentWeatherIcon(this.state.currentIcon) : this.state.loading} height="60px" />
     </div>

    </div>

    </div>


   <div className="sideContent">
     <ul>
     <li> <h2> Right Now </h2> </li>

    <div>
     <li> Visibility  </li>
     <p>{this.state.todayInfo ? this.state.todayInfo.current.vis_miles : this.state.loading} miles</p>
     </div>

     <div>
     <li> Humidity </li>
     <p> {this.state.todayInfo ? this.state.todayInfo.current.humidity : this.state.loading}&#37; </p>
     </div>

     <div>
     <li> Cloudy </li>
     <p> {this.state.todayInfo ? this.state.todayInfo.current.cloud : this.state.loading}&#37;</p>
     </div>

     <div>
     <li> Pressure</li>
     <p> {this.state.todayInfo ? this.state.todayInfo.current.pressure_in : this.state.loading} </p>
     </div>

     <div>
     <li> Wind gust</li>
     <p>{this.state.todayInfo ? this.state.todayInfo.current.gust_mph : this.state.loading}  miles </p>
     </div>
     </ul>


    </div>

   </div>

 <SideBody />


   <div className="secondRow">
     <div className="topInfo">
   <h3> Next 3 days</h3>
   <a href="#"> <h4> Next 10 days</h4> </a>
     </div>

    <div className="dateInfo">

     <div className="firstBox box">
       <p> TOMORROW </p>
       <img src={this.state.todayInfo ? this.currentWeatherIcon(this.state.conditionOneDayAhead.code) : this.state.loading} />

       <div className="temperature">
         <h5>{ this.state.todayInfo ? this.state.conditionOneDayAhead.text : this.state.loading}</h5>
