import React,{Component} from 'react';
import './App.css';
import Header from './head/Header.js'
import Footer from './footer/Footer.js'
import Today from './body/Today.js'
import axios from 'axios';



class App extends Component {

state ={
  loadingText:"updating...",
    searchByLL:"",
    id:"",
    city:"Lima"
}



componentDidMount() {
  // Fetching user location
  navigator.geolocation.getCurrentPosition((pos) => {
        this.setState({
            currentPosition: pos.coords.latitude.toString() + "," + pos.coords.longitude.toString(),
            uri:'http://api.apixu.com/v1/forecast.json?key=82fe923258fa4530a5e235322192604&q='
        });
        fetch("https://cors-anywhere.herokuapp.com/" + this.state.uri + this.state.currentPosition + '&days=7').then((res) => res.json())
        .then((todayInfo) => this.setState({
            todayInfo,
            currentIcon:todayInfo.current.condition.code
        }))
            .catch((err) => console.log(err.message))
    });

    // Fetching news
    var url = 'https://newsapi.org/v2/top-headlines?' +
              'country=us&' +
              'apiKey=f8822ecd8b3d4ceb87c8b11367961689';
    var req = new Request(url);
    fetch(req)
    .then((res) => res.json())
    .then((news) => this.setState({
        news:news.articles
    })).catch((err) => console.log(err.message));

    // Fetching advice
    fetch("https://cors-anywhere.herokuapp.com/" + "https://api.adviceslip.com/advice").then((res) => res.json())
    .then((advice) => this.setState({
       advice:advice.slip.advice
    }))
        .catch((err) => console.log(err.message));

      // Fetching advice
        fetch("https://cors-anywhere.herokuapp.com/" + "https://api.adviceslip.com/advice").then((res) => res.json())
        .then((advice) => this.setState({
           advice:advice.slip.advice
        }))
            .catch((err) => console.log(err.message));
}








render (){
  return (
    <div>
    <Header />
    <div className="App">
   <Today city={this.state.city} loadingText={this.state.loadingText} searchByLL={this.state.searchByLL} id={this.state.id}
    news={this.state.news}
    currentIcon={this.state.currentIcon}  currentPosition={this.state.currentPosition} advice={this.state.advice} todayInfo={this.state.todayInfo}
      />
    </div>
    <Footer />
    </div>
  );
}
}
export default App;
