import React, { Component } from 'react';
import GoogleMapReact from 'google-map-react';

const AnyReactComponent = ({ text }) => <div ><img style={{height:"40px"}}  src="https://image.flaticon.com/icons/svg/1329/1329665.svg"/></div>;
const handleApiLoaded = (map, maps) => {
  // use map and maps objects
};
class SimpleMap extends Component {

  static defaultProps = {
    center: {
      lat: 40.6462668,
      lng: -74.2586487
    },
    zoom: 11
  };

  render() {

    const data = map.map(place => ({
          lat: place.geometry.location.lat,
          lng: place.geometry.location.lng,
          weight: Math.floor(Math.random() * Math.floor(5)),
        }));
        const heatmapData = {
          positions: data,
          options: {
            radius: 20,
            opacity: 1,
          },
        };


    return (
      // Important! Always set the container height explicitly
      <div style={{ height: '100vh', width: '100%' }}>
        <GoogleMapReact
          bootstrapURLKeys={{ key:"AIzaSyACJaD84u2ynHqmHBGN3zrqaj1jbTicNRg"}}
          defaultCenter={this.props.center}
          defaultZoom={this.props.zoom}
          yesIWantToUseGoogleMapApiInternals
          onGoogleApiLoaded={({ map, maps }) => handleApiLoaded(map, maps)}
          layerTypes={['FusionTablesLayer', 'TransitLayer']}

  heatmapLibrary={true}
        >
          <AnyReactComponent
            lat={40.6462668}
            lng={-74.2586487}
            text
          />
        </GoogleMapReact>
      </div>
    );
  }
}

export default SimpleMap;
