import React,{Component} from 'react';
import {BrowserRouter as Router, Route } from 'react-router-dom'
import './App.css';
import Header from './head/Header.js'
import Footer from './footer/Footer.js'
import Today from './body/Today.js'
import axios from 'axios';


class App extends Component {
constructor(){

super()
 this.state ={
  loadingText:"updating...",
    searchByLL:"",
    id:"",
    city:""
}
  this.handleData = this.handleData.bind(this)
}


componentDidMount() {
  var apiRequest1 = fetch('http://api.apixu.com/v1/forecast.json?key=82fe923258fa4530a5e235322192604&q=Lima').then(function(response){
           return response.json()
  })  .then((example) => this.setState({
        example
    }))
        .catch((err) => console.log(err.message));
  var apiRequest2 = fetch('http://api.apixu.com/v1/forecast.json?key=82fe923258fa4530a5e235322192604&q=Lima').then(function(response){
           return response.json()
  });
  var combinedData = {"apiRequest1":{},"apiRequest2":{}};
  Promise.all([apiRequest1,apiRequest2]).then(function(values){
      combinedData["apiRequest1"] = values[0].current;
      combinedData["apiRequest2"] = values[1];
      console.log(combinedData);
  });
  // Fetching user location
  navigator.geolocation.getCurrentPosition((pos) => {
        this.setState({
            currentLatitude:pos.coords.latitude,
            currentLongitude:pos.coords.longitude,
            currentPosition: pos.coords.latitude.toString() + "," + pos.coords.longitude.toString(),
            uri:'http://api.apixu.com/v1/forecast.json?key=82fe923258fa4530a5e235322192604&q='
        });
        fetch("https://cors-anywhere.herokuapp.com/" + this.state.uri + this.state.currentPosition + '&days=7').then((res) => res.json())
        .then((todayInfo) => this.setState({
            todayInfo,
            currentIcon:todayInfo.current.condition.code,
            forecast:todayInfo.forecast.forecastday,
            conditionOneDayAhead:todayInfo.forecast.forecastday[1].day.condition,
            conditionTwoDayAhead:todayInfo.forecast.forecastday[2].day.condition,
            conditionThreeDayAhead:todayInfo.forecast.forecastday[3].day.condition,
            conditionFourthDayAhead:todayInfo.forecast.forecastday[4].day.condition,
            conditionFifthDayAhead:todayInfo.forecast.forecastday[5].day.condition,
            conditionSixthDayAhead:todayInfo.forecast.forecastday[6].day.condition
        }))
            .catch((err) => console.log(err.message))
    });

    // Fetching news
    var url = 'https://newsapi.org/v2/top-headlines?' +
              'country=us&' +
              'apiKey=f8822ecd8b3d4ceb87c8b11367961689';
    var req = new Request(url);
    fetch(req)
    .then((res) => res.json())
    .then((news) => this.setState({
        news:news.articles
    })).catch((err) => console.log(err.message));

    // Fetching advice
    fetch("https://cors-anywhere.herokuapp.com/" + "https://api.adviceslip.com/advice").then((res) => res.json())
    .then((advice) => this.setState({
       advice:advice.slip.advice
    }))
        .catch((err) => console.log(err.message));

    // Fetching advice
        fetch("https://cors-anywhere.herokuapp.com/" + "http://quotes.rest/qod.json").then((res) => res.json())
        .then((quote) => this.setState({
           quote:quote.contents.quotes[0]
        }))
            .catch((err) => console.log(err.message));
}





handleData(data) {
  this.setState({
    city: data
  });
  fetch( this.state.uri + this.state.city+ '&days=7').then((res) => res.json())
  .then((todayInfo) => this.setState({
      todayInfo,
      currentIcon:todayInfo.current.condition.code
  }))
      .catch((err) => console.log(err.message))
}



render (){

  return (
    <div>
    <Router >
   <Route exact path="/" render={props => (
       <React.Fragment>
         <Header todayInfo={this.state.todayInfo} handlerFromHead={this.handleData} currentIcon={this.state.currentIcon}  currentLocation={this.state.currentLocation} loading={this.state.loading}/>
         <div className="App">
         <Today handlerFromHead={this.handleData} city={this.state.city} loadingText={this.state.loadingText} searchByLL={this.state.searchByLL} id={this.state.id}
         news={this.state.news} quote={this.state.quote} currentLatitude={this.state.currentLatitude} currentLongitude={this.state.currentLongitude}
         currentIcon={this.state.currentIcon}  currentPosition={this.state.currentPosition} advice={this.state.advice} todayInfo={this.state.todayInfo}
           conditionOneDayAhead={this.state.conditionOneDayAhead} conditionTwoDayAhead={this.state.conditionTwoDayAhead} conditionThreeDayAhead={this.state.conditionThreeDayAhead}
           conditionFourthDayAhead={this.state.conditionFourthDayAhead} conditionFifthDayAhead={this.state.conditionFifthDayAhead} conditionSixthDayAhead={this.state.conditionSixthDayAhead} />
         </div>
         <Footer />
              </React.Fragment>
     )} />
     <Route  path="/about"
       render ={(routeProps) => ( <Header {...routeProps} todayInfo={this.state.todayInfo} handlerFromHead={this.handleData} currentIcon={this.state.currentIcon}  currentLocation={this.state.currentLocation} loading={this.state.loading}/> )}
       />
  <Route  path="/about"
    render ={(routeProps) => ( <Footer {...routeProps} quote={this.state.quote} todayInfo={this.state.todayInfo} city={this.state.city} loadingText={this.state.loadingText} searchByLL={this.state.searchByLL} id={this.state.id}
    news={this.state.news} quote={this.state.quote} currentLatitude={this.state.currentLatitude} currentLongitude={this.state.currentLongitude}
    currentIcon={this.state.currentIcon}  currentPosition={this.state.currentPosition} advice={this.state.advice} todayInfo={this.state.todayInfo} /> )}
    />
   </Router>


    </div>
  );
}
}
export default App;
